const api = {
  key : "24632732eb9a833f013d856c333f40eb",
  base: "http://api.openweathermap.org/data/2.5/"
}


function App() {

  const[query, setQuery] = useState('');
  const[weather, setWeather] = useState({});

  const search = evt => {
