import React from 'react';

const api = {
  key : "24632732eb9a833f013d856c333f40eb",
  base: "http://api.openweathermap.org/data/2.5/"
}


function App() {
  var d = new Date();
  var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
  var days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  return (
    <div className="app">
      <main>
        <div className="search-box">
          <input type = "text" className="search-bar" placeholder = "Enter the name of a city...">
          </input>
        </div>

        <div>
          <div className="location-box">
            <div className="location">"city name"</div>
            <div className="date">{`${days[d.getDay()]}. ${d.getDate()} ${months[d.getMonth()]}, ${d.getFullYear()}`}</div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
